<script>
  import * as d3 from "d3";
  import CancionesPorDecada from "./components/CancionesPorDecada.svelte";
  import Footer from "./components/Footer.svelte";
  import { onMount } from 'svelte';
  import { reproduccionesPorPersona } from "./stores.js"; // Para acumular los clics de las personas
  
  // Variables generales
  let canciones = [];
  let cancionesPorDecada = {};
  let decadas = [];
  let escalaReproducciones;
  let estadosReproduccion = {};
  let generoSeleccionado = ""; 
  let decadaSeleccionada = "all";
  let elegidoPorSeleccionado = "";

   
    // Variables para el juego final 
    let totalReproducciones = 0; 
    let afinidadSteffy = 0;
    let afinidadRosita = 0;
    let afinidadVar = 0;
    let personaMasAfin = {
        nombre: "Nadie",
        simbolo: "",
        porcentaje: 0
    };

  // Nuevas variables para la funcionalidad de canción favorita
  let userName = ''; // Agregado: para el nombre de usuario
    let favoriteSongTitle = '';
    let favoriteSongArtist = '';
    let favoriteSongGenre = ''; // Agregado: para el género
    let songReleaseYear = ''; // Agregado: para el año de lanzamiento
    let userFavoriteSongs = []; // Array para almacenar las canciones favoritas de los usuarios

    // Variables para estadísticas de género (del ejemplo anterior, si las necesitas)
    let genreCounts = {};
    let totalSharedSongs = 0;

    // AQUI ES DONDE PONES EL LINK DE TU GOOGLE APPS SCRIPT
    const GOOGLE_APPS_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbxHQ7BSPmxFIYutkcsOOylaiEEEphCsOkECQyZBtgdyjQB3xEvMGe4uc9EgpwgvCg1oMQ/exec';


    // Cargar canciones favoritas del localStorage al inicio
    onMount(() => {
        // loadFlourishScrolly(); // Mantén esta línea si es relevante para tu proyecto
        const storedSongs = localStorage.getItem('userFavoriteSongs');
        if (storedSongs) {
            userFavoriteSongs = JSON.parse(storedSongs);
            // Si la estructura de los objetos en localStorage ha cambiado,
            // considera una migración simple o un valor por defecto.
            // Por ejemplo, para asegurarte de que tengan 'AñoLanzamiento' y 'GeneroCancion'
            userFavoriteSongs = userFavoriteSongs.map(song => ({
                NombreUsuario: song.NombreUsuario || 'Anónimo', // Valor por defecto si no existe
                TituloCancion: song.TituloCancion || song.title, // Asegura compatibilidad con 'title' antiguo
                ArtistaCancion: song.ArtistaCancion || song.artist, // Asegura compatibilidad con 'artist' antiguo
                GeneroCancion: song.GeneroCancion || 'Desconocido', // Valor por defecto si no existe
                AnioLanzamiento: song.AnioLanzamiento || '', // Valor por defecto si no existe
                Timestamp: song.Timestamp || new Date().toISOString()
            }));
            updateGenreStats(); // Llama para actualizar las estadísticas al cargar
        }
    });

    async function addFavoriteSong() {
    if (userName.trim() !== "" &&
        favoriteSongTitle.trim() !== "" &&
        favoriteSongArtist.trim() !== "" &&
        favoriteSongGenre.trim() !== "" &&
        songReleaseYear.trim() !== "") {

        const newSong = {
            NombreUsuario: userName, // Clave: valor (importante que la clave coincida con lo que espera Apps Script)
            TituloCancion: favoriteSongTitle,
            ArtistaCancion: favoriteSongArtist,
            GeneroCancion: favoriteSongGenre,
            AnioLanzamiento: songReleaseYear, // Envía el año
            
        };

        try {
            const response = await fetch(GOOGLE_APPS_SCRIPT_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json', 
                },
                body: JSON.stringify(newSong), // <--- ¡Envía el objeto como JSON!
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Datos guardados en Google Sheet:', result);
                alert('¡Canción agregada con éxito!');

    
                userFavoriteSongs = [...userFavoriteSongs, {
                    NombreUsuario: userName,
                    TituloCancion: favoriteSongTitle,
                    ArtistaCancion: favoriteSongArtist,
                    GeneroCancion: favoriteSongGenre,
                    AnioLanzamiento: songReleaseYear,
                   
                }];
                localStorage.setItem('userFavoriteSongs', JSON.stringify(userFavoriteSongs));
                updateGenreStats();

                // Limpiar los inputs
                userName = '';
                favoriteSongTitle = '';
                favoriteSongArtist = '';
                favoriteSongGenre = '';
                songReleaseYear = '';
            } else {
                console.error('Error al guardar la canción:', response.status, response.statusText);
                alert('Hubo un error al guardar tu canción. Intenta de nuevo.');
            }
        } catch (error) {
            console.error('Error de red o al enviar datos:', error);
            alert('No se pudo conectar con el servidor. Verifica tu conexión.');
        }
    } else {
        alert("Por favor, completa todos los campos: Nombre, Título, Artista, Género y Año de Lanzamiento.");
    }
}
    // ... (tus funciones removeFavoriteSong y updateGenreStats se mantienen igual)
    function removeFavoriteSong(index) {
        userFavoriteSongs = userFavoriteSongs.filter((_, i) => i !== index);
        localStorage.setItem('userFavoriteSongs', JSON.stringify(userFavoriteSongs));
        updateGenreStats();
    }

    function updateGenreStats() {
        const counts = {};
        userFavoriteSongs.forEach(song => {
            counts[song.GeneroCancion] = (counts[song.GeneroCancion] || 0) + 1;
        });
        genreCounts = counts;
        totalSharedSongs = userFavoriteSongs.length;
    }
    const simboloSelector = {
    "Var": "/images/Var.png",
    "Rosita": "/images/Rosita.png",
    "Steffy": "/images/Steffy.png"
  };
  
  const iconosPlayPause = {
    play: "/images/Var.png",
    pause: "/images/Pause.png"
  };

    // Cálculos de afinidad
    $: {
        totalReproducciones = $reproduccionesPorPersona.Steffy + $reproduccionesPorPersona.Rosita + $reproduccionesPorPersona.Var;

        if (totalReproducciones > 0) {
            afinidadSteffy = ($reproduccionesPorPersona.Steffy / totalReproducciones) * 100;
            afinidadRosita = ($reproduccionesPorPersona.Rosita / totalReproducciones) * 100;
            afinidadVar = ($reproduccionesPorPersona.Var / totalReproducciones) * 100;

            const afinidades = [
                { nombre: "Steffy", porcentaje: afinidadSteffy, simbolo: simboloSelector.Steffy },
                { nombre: "Rosita", porcentaje: afinidadRosita, simbolo: simboloSelector.Rosita },
                { nombre: "Var", porcentaje: afinidadVar, simbolo: simboloSelector.Var }
            ];

            // Ordenar de mayor a menor y asignar la personaMasAfin
            // Ordenar de mayor a menor
            afinidades.sort((a, b) => b.porcentaje - a.porcentaje);

            const top1 = afinidades[0];
            const top2 = afinidades[1];

            if (top1.porcentaje === top2.porcentaje) {
              personaMasAfin = {
                  nombre: "Tenés gustos mixtos",
                  simbolo: "",
                  porcentaje: 0
              };
          } else {
              personaMasAfin = {
                  nombre: "Te identificás más con " + top1.nombre,
                  simbolo: top1.simbolo,
                  porcentaje: top1.porcentaje
              };
          }



        } else {
            // Resetear si no hay reproducciones
            afinidadSteffy = 0;
            afinidadRosita = 0;
            afinidadVar = 0;
            personaMasAfin = { nombre: "Nadie", porcentaje: 0, simbolo: "" };
        }
      
    }


  d3.csv("/Datos.csv").then(data => {
    //test
    canciones = data;
    canciones.forEach(c => {
      const decada = c.decadas;
      if (!cancionesPorDecada[decada]) {
        cancionesPorDecada[decada] = [];
        decadas.push(decada);
      }
      cancionesPorDecada[decada].push(c);
    });

// Ordenar las canciones dentro de cada década por número de reproducciones (de menor a mayor)
Object.keys(cancionesPorDecada).forEach(decada => {
  cancionesPorDecada[decada].sort((a, b) => {
    const repA = +a.reproducciones.replace(/[^0-9]/g, "");
    const repB = +b.reproducciones.replace(/[^0-9]/g, "");
    return repA - repB; 
  });
});

    const ordenPersonalizado = ["20", "10", "00", "90", "80"];
    decadas.sort((a, b) => ordenPersonalizado.indexOf(a) - ordenPersonalizado.indexOf(b));
    
    const maxReproducciones = d3.max(canciones, d => +d.reproducciones.replace(/[^0-9]/g, ""));
    escalaReproducciones = d3.scaleSqrt()
      .domain([0, maxReproducciones])
      .range([60, 200]);
  });
  
  const colorGenero = d3.scaleOrdinal()
    .domain(["Pop", "Rock", "Indie", "Electrónica", "Reguetón", "Rap"])
    .range(["#00CC66", "#CC0000", "#CC0066", "#001BCC", "#CCB400", "#000000"]);
  
 
  
  function obtenerDiametro(reproducciones) {
    const numero = reproducciones.replace(/[^0-9]/g, "");
    return escalaReproducciones(numero);
  }
  
  
  const slides = [
    "Explora un mapa interactivo con las canciones más escuchadas de 2025 en diversos países del mundo. Descubre los éxitos globales que están marcando el pulso musical del planeta este año.",
    "En 2025, la canción más escuchada en Argentina fue “DTMF (Debí Tirar Más Fotos)” de Bad Bunny. Con un reguetón nostálgico pero bailable, el tema se volvió un clásico instantáneo: para perrear con el corazón roto y mover los recuerdos al ritmo del beat",
    "Desde Estados Unidos, Beautiful Things de Benson Boone se convirtió en una de las canciones más escuchadas del 2025. Con su voz intensa y una letra que abraza el alma, la canción conectó con millones en todo el mundo. Una balada emotiva que habla de amor, pérdida y gratitud por las pequeñas cosas que hacen hermosa la vida.",
    "Desde Francia, Est-ce que tu m’aimes? de GIMS volvió a sonar con fuerza en 2025. Un clásico moderno que mezcla melancolía y ritmo, preguntando con voz profunda lo que tantos temen decir: “¿Me amás?”. La canción cruzó fronteras con su estilo inconfundible, dejando eco en corazones de todo el mundo.",
    "Desde Arabia Saudita, Die With a Smile de Lady Gaga y Bruno Mars conquistó el 2025 con una fusión inesperada de pop, soul y ritmos árabes. Un himno brillante que celebra la vida con estilo, actitud y una sonrisa final. Misterioso, magnético y poderoso… como el desierto al atardecer.",
    "Desde Uganda llegó Baby (It Is a Crime), un hit que mezcló ritmos afrobeat con una historia de amor intenso y peligroso. Con beats vibrantes y una letra que duele, la canción se volvió himno en las pistas de África y más allá. Porque a veces… amar también puede ser un crimen.",
    "Desde Japón, Mona Lisa de J-Hope se convirtió en una obra maestra del 2025. Un tema enigmático, con ritmos suaves y elegancia coreografiada, donde cada verso es una pincelada. Con su sonrisa críptica y su flow brillante, J-Hope hizo del silencio... puro arte pop.",
    "Desde Nueva Zelanda, Ordinary de Alex Warren tocó fibras profundas en 2025. Una balada honesta sobre sentirse común en un mundo que exige brillar. Con guitarra suave y voz quebrada, convirtió lo simple en algo hermoso. Porque ser “ordinary” también es parte de lo extraordinario."
    ]

  function loadFlourishScrolly() {
      const script = document.createElement('script')
      script.src = "https://cdn.flourish.rocks/flourish-scrolly-v3.1.0.min.js"
      script.type = "text/javascript"
      script.onload = () => initFlourishScrolly()
      document.body.appendChild(script)
  }

  onMount(() => {
    loadFlourishScrolly()
  });

  
</script>

